import React from 'react';
import '../style.css';

function MediaPlayer({ file }) {
  if (!file) {
    return <p>Select a video or audio file to play.</p>;
  }

  return (
    <div className="media-player">
      {file.endsWith('.mp4') || file.endsWith('.webm') || file.endsWith('.mkv') ? (
        <video className="video-player" controls>
          <source src={`${process.env.REACT_APP_BACKEND_URL}/media/${file}`} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      ) : file.endsWith('.mp3') || file.endsWith('.wav') ? (
        <audio className="audio-player" controls>
          <source src={`${process.env.REACT_APP_BACKEND_URL}/media/${file}`} type="audio/mpeg" />
          Your browser does not support the audio tag.
        </audio>
      ) : (
        <p>Unsupported file type.</p>
      )}
    </div>
  );
}

export default MediaPlayer;
