import React from 'react';
import '../style.css';

function MediaPlayer({ file }) {
  if (!file) {
    return <p>Select a video or audio file to play.</p>;
  }

  // Construct media URL using the media_id
  const mediaUrl = `${process.env.REACT_APP_BACKEND_URL}/media/${file}`;

  // Determine media type based on the file extension
  const isVideo = mediaUrl.endsWith('.mp4') || mediaUrl.endsWith('.webm') || mediaUrl.endsWith('.mkv');
  const isAudio = mediaUrl.endsWith('.mp3') || mediaUrl.endsWith('.wav');

  return (
    <div className="media-player">
      {isVideo ? (
        <video className="video-player" controls>
          <source src={mediaUrl} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      ) : isAudio ? (
        <audio className="audio-player" controls>
          <source src={mediaUrl} type="audio/mpeg" />
          Your browser does not support the audio tag.
        </audio>
      ) : (
        <p>Unsupported file type.</p>
      )}
    </div>
  );
}

export default MediaPlayer;
