import React from 'react';
import '../style.css';

function MediaPlayer({ file }) {
  return (
    <div className="media-player">
      {file ? (
        file.type.startsWith('video/') || file.name.endsWith('.mkv') ? (
          <video className="video-player" controls>
            <source src={`${process.env.REACT_APP_BACKEND_URL}/media/${file}`} type={file.type || 'video/mp4'} />
            Your browser does not support the video tag.
          </video>
        ) : file.type.startsWith('audio/') || file.name.endsWith('.mp3') ? (
          <audio className="audio-player" controls>
            <source src={`http://127.0.0.1:8000/media/${file}`} type={file.type || 'audio/mpeg'} />
            Your browser does not support the audio tag.
          </audio>
        ) : (
          <p>Unsupported file type.</p>
        )
      ) : (
        <p>Select a video or audio file to play.</p>
      )}
    </div>
  );
}

export default MediaPlayer;
